import NavBar from './components/NavBar'
import Main from './components/Main'
import { BrowserRouter, useLocation } from 'react-router-dom'

function App() {
  const loc = useLocation();

  let currLoc = -1;
  if (useLocation().pathname.endsWith('/viewdb')) {
    currLoc = 0;
  }
  else if (useLocation().pathname.endsWith('/additem')) {
    currLoc = 1;
  }
  else if (useLocation().pathname.endsWith('/remitem')) {
    currLoc = 2;
  }
  return (
    <>
      <BrowserRouter>
        <NavBar />
      </BrowserRouter>
      {currLoc == 0 ? <Main /> : (currLoc == 1 ? <AddItem /> : <RemItem />)}
    </>
  );
}

export default App;
