import { onMouseEnter, useState } from 'react'
import { Link } from 'react-router-dom'

function NavBar() {

    const [selectedIndex, setSelectedIndex] = useState(-1);

    const checkActive = (index, item) => {
        if (selectedIndex === index) {
            item.addClass("active")
        }
    }

    return (
        <nav className="navbar navbar-expand-sm navbar-light bg-primary">
            <div className="container-fluid">
                <Link to="" className="navbar-brand" style={{color:"white", fontWeight:"bold"}}>BookTracker</Link>
                <ul className="navbar-nav">
                    <li className="navbar-item px-2"><Link to="/viewdb" className="nav-link" style={{color:"white", fontWeight:"500"}} 
                        onMouseEnter={() => setSelectedIndex(0)} onMouseLeave={() => this.removeClass("active")}>View Read Books</Link></li>
                    <li className="navbar-item px-2"><Link to="/additem" className="nav-link" style={{color:"white", fontWeight:"500"}} 
                        onMouseEnter={() => setSelectedIndex(1)} onMouseLeave={() => this.removeClass("active")}>Add Books</Link></li>
                    <li className="navbar-item px-2"><Link to="/remitem" className="nav-link" style={{color:"white", fontWeight:"500"}} 
                        onMouseEnter={() => setSelectedIndex(2)} onMouseLeave={() => this.removeClass("active")}>Remove Books</Link></li>
                </ul>
            </div>
        </nav>
    );
};

export default NavBar;
