import { onMouseEnter, onMouseLeave, useState } from 'react'
import { Link } from 'react-router-dom'

function NavBar() {

    const [selectedIndex, setSelectedIndex] = useState(-1);

    const checkActive = (index, item) => {
        if (selectedIndex === index) {
            item.addClass("active")
        }
        else if (selectedIndex != index) {
            item.removeClass("active")
        }
    }

    return (
        <nav className="navbar navbar-expand-sm navbar-light bg-primary">
            <div className="container-fluid">
                <Link to="" className="navbar-brand" style={{color:"white", fontWeight:"bold"}}>BookTracker</Link>
                <ul className="navbar-nav">
                    <li className="navbar-item px-2" id={0} onMouseEnter={() => {
                        setSelectedIndex(0);
                        checkActive(id, this);
                    }} onMouseLeave={() => {
                        setSelectedIndex(-1);
                        checkActive(id, this);
                    }}>
                        <Link to="/viewdb" className="nav-link" style={{color:"white", fontWeight:"500"}} >View Read Books</Link></li>
                    <li className="navbar-item px-2" id={1} onMouseEnter={() => {
                        setSelectedIndex(1); 
                        checkActive(id, this);
                    }} onMouseLeave={() => {
                        setSelectedIndex(-1); 
                        checkActive(id, this);
                    }}>
                        <Link to="/additem" className="nav-link" style={{color:"white", fontWeight:"500"}}>Add Books</Link></li>
                    <li className="navbar-item px-2" id={2} onMouseEnter={() => {
                        setSelectedIndex(2);
                        checkActive();
                    }} onMouseLeave={() => {
                        setSelectedIndex(-1);
                        checkActive();
                    }}><Link to="/remitem" className="nav-link" style={{color:"white", fontWeight:"500"}}>Remove Books</Link></li>
                </ul>
            </div>
        </nav>
    );
};

export default NavBar;
