import { onMouseEnter, useState } from 'react'
import { Link } from 'react-router-dom'

function NavBar() {

    const [selectedIndex, setSelectedIndex] = useState(-1);

    const checkActive = (index) => {
        return selectedIndex === index ? "navbar-item px-2 active" : "navbar-item px-2"
    }

    return (
        <nav className="navbar navbar-expand-sm navbar-dark">
            <div className="container-fluid">
                <Link to="" className="navbar-brand" style={{color:"black", fontWeight:"bold"}}>BookTracker</Link>
                <ul className="navbar-nav">
                    <li className="navbar-item px-2 active"><Link to="/viewdb" className="nav-link" style={{color:"black", fontWeight:"500"}} onMouseEnter={() => setSelectedIndex(0)}>View Read Books</Link></li>
                    <li className={selectedIndex === 1 ? "navbar-item px-2 active" : "navbar-item px-2"}><Link to="/additem" className="nav-link" style={{color:"black", fontWeight:"500"}} onMouseEnter={() => setSelectedIndex(1)}>Add Books</Link></li>
                    <li className={selectedIndex === 2 ? "navbar-item px-2 active" : "navbar-item px-2"}><Link to="/remitem" className="nav-link" style={{color:"black", fontWeight:"500"}} onMouseEnter={() => setSelectedIndex(2)}>Remove Books</Link></li>
                </ul>
            </div>
        </nav>
    );
};

export default NavBar;
