import NavBar from './components/NavBar'
import Main from './components/Main'
import AddItem from './components/AddItem'
import RemItem from './components/RemItem'
import ViewDB from './components/ViewDB';
import { useLocation } from 'react-router-dom'

function App() {
  const loc = useLocation();
  let currLoc = "";

  if (loc.pathname.endsWith("/viewdb")) {
    currLoc = "viewdb";
  }
  else if (loc.pathname.endsWith("/additem")) {
    currLoc = "additem";
  }
  else if (loc.pathname.endsWith("/remitem")) {
    currLoc = "remitem";
  }
  else {
    currLoc = ""
  }
  return (
    <>
        <NavBar />
        {currLoc == "viewdb" ? <ViewDB /> : (currLoc == "additem" ? <AddItem /> : (currLoc == "remitem" ? <RemItem /> : <Main />))};
    </>
  );
}

export default App;
