import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function NavBar() {
    const [hoveredIndex, setHoveredIndex] = useState(null);

    return (
        <nav className="navbar navbar-expand-sm navbar-light bg-primary">
            <div className="container-fluid">
                <Link to="" className="navbar-brand" style={{ color: "white", fontWeight: "bold" }}>BookTracker</Link>
                <ul className="navbar-nav">
                    <li
                        className="navbar-item px-2"
                        onMouseEnter={() => setHoveredIndex(0)}
                        onMouseLeave={() => setHoveredIndex(null)}
                        style={{
                            backgroundColor: hoveredIndex === 0 ? '#007bff' : 'transparent',
                            color: hoveredIndex === 0 ? '#fff' : 'white'
                        }}
                    >
                        <Link to="/viewdb" className="nav-link" style={{ color: "inherit" }}>View Read Books</Link>
                    </li>
                    <li
                        className="navbar-item px-2"
                        onMouseEnter={() => setHoveredIndex(1)}
                        onMouseLeave={() => setHoveredIndex(null)}
                        style={{
                            backgroundColor: hoveredIndex === 1 ? '#007bff' : 'transparent',
                            color: hoveredIndex === 1 ? '#fff' : 'white'
                        }}
                    >
                        <Link to="/additem" className="nav-link" style={{ color: "inherit" }}>Add Books</Link>
                    </li>
                    <li
                        className="navbar-item px-2"
                        onMouseEnter={() => setHoveredIndex(2)}
                        onMouseLeave={() => setHoveredIndex(null)}
                        style={{
                            backgroundColor: hoveredIndex === 2 ? '#007bff' : 'transparent',
                            color: hoveredIndex === 2 ? '#fff' : 'white'
                        }}
                    >
                        <Link to="/remitem" className="nav-link" style={{ color: "inherit" }}>Remove Books</Link>
                    </li>
                </ul>
            </div>
        </nav>
    );
}

export default NavBar;


